import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [userMessage, setUserMessage] = useState('');
  const [botResponse, setBotResponse] = useState('');

  const handleSendMessage = async () => {
    try {
      const sessionId = '123456'; // Unique session ID (e.g., user ID or UUID)
      const response = await axios.post('http://localhost:5001/api/dialogflow', {
        text: userMessage,
        sessionId: sessionId,
      });
      setBotResponse(response.data.reply); // Set bot's reply
    } catch (error) {
      console.error('Error communicating with backend:', error);
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h1>Chatbot</h1>
      <div>
        <input
          type="text"
          value={userMessage}
          onChange={(e) => setUserMessage(e.target.value)}
          placeholder="Type your message here..."
          style={{
            padding: '10px',
            width: '300px',
            marginRight: '10px',
          }}
        />
        <button onClick={handleSendMessage} style={{ padding: '10px 20px' }}>
          Send
        </button>
      </div>
      <div style={{ marginTop: '20px' }}>
        <strong>Bot Response:</strong>
        <p>{botResponse || 'No response yet...'}</p>
      </div>
    </div>
  );
};

export default App;
