import React, { useState } from 'react';
import axios from 'axios';

const Signup = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });

  const [isLoading, setIsLoading] = useState(false); // State to manage loading spinner
  const [isSuccess, setIsSuccess] = useState(false); // State to display success message
  const [check, setCheck] = useState(3); // Code response from backend

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setIsSuccess(false);

    try {
      const response = await axios.post('http://localhost:5019/api/createAccount', formData);
      console.log('Backend Response:', response.data);

      // Handle backend response
      if (response.data.code === 1) {
        setIsSuccess(true); // Display success message
        setCheck(1); // Code for success
        setFormData({ username: '', email: '', password: '' }); // Clear form fields

        // Re-show the form after 2 seconds
        setTimeout(() => {
          setIsSuccess(false);
          setCheck(3); // Reset check to show the form again
        }, 2000);
      } else if (response.data.code === 0) {
        setIsSuccess(false); // No success
        setCheck(0); // Code for user already exists
        setFormData({ username: '', email: '', password: '' }); // Clear form fields

        // Refresh page after 2 seconds
        setTimeout(() => {
          setCheck(3); // Reset check to show the form again
        }, 2000);
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Failed to create account!');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="relative min-h-screen bg-gradient-to-b from-black via-gray-900 to-black flex items-center justify-center p-5">
      {/* Background Content */}
      <div className="absolute inset-0 text-gray-400 flex items-center justify-center flex-col space-y-4 z-0">
        <h1 className="text-4xl font-bold opacity-20">Chatbot Application</h1>
        <p className="text-lg max-w-xl text-center opacity-30">
          Welcome to the future of communication! Create your account and start interacting with intelligent, responsive, and dynamic chatbots that are designed to assist, support, and engage.
        </p>
      </div>

      {/* Account Creation Form */}
      <div className="relative z-10 bg-gray-800 text-white p-10 rounded-lg shadow-lg transform transition-all hover:scale-105">
        <h2 className="text-2xl font-bold mb-6 text-center">Create Your Account</h2>

        {isLoading && (
          // Loading Spinner
          <div className="flex justify-center items-center">
            <div className="animate-spin rounded-full h-10 w-10 border-t-4 border-gray-500"></div>
          </div>
        )}

        {isSuccess && check === 1 && (
          // Success Message
          <div className="text-center text-green-400 font-bold">
            Account created successfully!
          </div>
        )}

        {!isSuccess && check === 0 && (
          // User Already Exists Message
          <div className="text-center text-red-400 font-bold">
            User already exists!
          </div>
        )}

        {!isSuccess && !isLoading && check === 3 && (
          <form onSubmit={handleSubmit} className="space-y-4">
            <input
              type="text"
              name="username"
              placeholder="Username"
              value={formData.username}
              onChange={handleChange}
              required
              className="w-full p-3 rounded-md bg-gray-700 text-white focus:ring-2 focus:ring-gray-500 focus:outline-none"
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
              className="w-full p-3 rounded-md bg-gray-700 text-white focus:ring-2 focus:ring-gray-500 focus:outline-none"
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
              className="w-full p-3 rounded-md bg-gray-700 text-white focus:ring-2 focus:ring-gray-500 focus:outline-none"
            />
            <button
              type="submit"
              className="w-full p-3 rounded-md bg-gray-500 hover:bg-gray-600 text-white font-semibold transition-all"
            >
              Create Account
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default Signup;
