import React, { useState } from 'react';
import axios from 'axios';

const AccountCreation = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/createAccount', formData);
      console.log('Account created:', response.data);
      alert('Account successfully created!');
    } catch (error) {
      console.error('Error:', error);
      alert('Failed to create account!');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-black via-gray-900 to-black flex items-center justify-center p-5">
      <div className="bg-gray-800 text-white p-10 rounded-lg shadow-lg transform transition-all hover:scale-105">
        <h2 className="text-2xl font-bold mb-6 text-center">Create Your Account</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={formData.username}
            onChange={handleChange}
            required
            className="w-full p-3 rounded-md bg-gray-700 text-white focus:ring-2 focus:ring-gray-500 focus:outline-none"
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
            className="w-full p-3 rounded-md bg-gray-700 text-white focus:ring-2 focus:ring-gray-500 focus:outline-none"
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
            className="w-full p-3 rounded-md bg-gray-700 text-white focus:ring-2 focus:ring-gray-500 focus:outline-none"
          />
          <button
            type="submit"
            className="w-full p-3 rounded-md bg-gray-500 hover:bg-gray-600 text-white font-semibold transition-all"
          >
            Create Account
          </button>
        </form>
      </div>
    </div>
  );
};

export default AccountCreation;
