import React, { useState } from 'react';
import axios from 'axios';

const VerifyCode = () => {
  const [verificationCode, setVerificationCode] = useState('');
  const [loading, setLoading] = useState(false); // State for spinner
  const [responseMessage, setResponseMessage] = useState(''); // State to show the server response

  const handleChange = (e) => {
    setVerificationCode(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Show spinner
    setResponseMessage(''); // Clear previous response
    try {
      let res = await axios.post('http://localhost:5030/verify', { code: verificationCode });
      setResponseMessage(res.data.message); // Set response message
    } catch (error) {
      setResponseMessage('An error occurred. Please try again.'); // Handle errors gracefully
    } finally {
      setLoading(false); // Hide spinner
    }
  };

  return (
    <div className="relative min-h-screen bg-gradient-to-b from-black via-gray-900 to-black flex items-center justify-center p-5">
      <div className="relative z-10 bg-gray-800 text-white p-10 rounded-lg shadow-lg">
        <h2 className="text-2xl font-bold mb-6 text-center">Enter Verification Code</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            name="verificationCode"
            placeholder="Enter your code"
            value={verificationCode}
            onChange={handleChange}
            required
            className="w-full p-3 rounded-md bg-gray-700 text-white focus:ring-2 focus:ring-gray-500 focus:outline-none"
          />
          <button
            type="submit"
            className="w-full p-3 rounded-md bg-gray-500 hover:bg-gray-600 text-white font-semibold transition-all"
            disabled={loading} // Disable button when loading
          >
            {loading ? 'Verifying...' : 'Verify Code'}
          </button>
        </form>
        {loading && <div className="text-center mt-4">ðŸ”„ Loading...</div>} {/* Spinner */}
        {responseMessage && <div className="text-center mt-4">{responseMessage}</div>} {/* Display server response */}
      </div>
    </div>
  );
};

export default VerifyCode;
